const fs = require('fs');
const content = fs.readFileSync('index.html', 'utf-8');
if (!content.includes('<h1>') || !content.includes('LogiCore Tech')) {
  throw new Error('h1 태그와 LogiCore Tech 텍스트가 필요합니다.');
}
if (content.match(/<p>/g).length < 2) {
  throw new Error('최소 2개의 p 태그가 필요합니다.');
}

const fs = require('fs');
const content = fs.readFileSync('style.css', 'utf-8');
if (!content.includes('h1') || !content.includes('color')) {
  throw new Error('h1 선택자와 color 속성이 필요합니다.');
}
if (!content.includes('p') || !content.includes('font-size')) {
  throw new Error('p 선택자와 font-size 속성이 필요합니다.');
}
if (!content.includes('body')) {
  throw new Error('body 선택자가 필요합니다.');
}

